import React, { useContext, useState } from 'react';
import './Sidebar.css';
import Logo from '../assets/Logo.jpeg';
import axios from 'axios';
import { MovieContext } from '../Provider/MovieProvider';
import Footer from '../Footer/Footer';
import { Link } from 'react-router-dom';

function Sidebar() {
  const randomIds = ['tt0111161', 'tt0068646', 'tt0071562'];
  const [randomData, setRandomData] = useState();

  const [movieData, setMovieData] = useContext(MovieContext);

  const getRandomID = () => {
    const randomId = randomIds[Math.floor(Math.random() * randomIds.length)];
    console.log(randomId);
    return randomId;
  };

  const handleValue = (e) => {
    let input = e.currentTarget.textContent;
    search(input);
  };

  const randomSearch = () => {
    const value = getRandomID();
    const url = `http://www.omdbapi.com/?apikey=8a2bf193&i=` + value;
    console.log(`http://www.omdbapi.com/?apikey=8a2bf193&i=` + value);
  };

  const search = (input) => {
    let value = input;
    const url = `http://www.omdbapi.com/?apikey=8a2bf193&s=` + value;
    axios.get(url).then((res) => {
      setMovieData(res.data.Search);
    });
  };

  return (
    <div className='sidebar'>
      <Link to={'/'}>
        <div className='logo-container'>
          {' '}
          <img className='logo-img' src={Logo} alt='logo' />
        </div>
      </Link>
      <div className='sidebar-searches'>Popular searches</div>

      <ul className='popular-searches'>
        <Link to={`/`} onClick={handleValue}>
          <li className='popular-movie' onClick={handleValue}>
            Superman
          </li>{' '}
        </Link>
        <Link to={`/`} onClick={handleValue}>
          <li onClick={handleValue}>Star Wars</li>
        </Link>
        <Link to={`/`} onClick={handleValue}>
          <li onClick={handleValue}>Harry Potter</li>
        </Link>
        <Link to={`/`} onClick={handleValue}>
          <li onClick={handleValue}>Twilight</li>
        </Link>
      </ul>
      <div>
        <div onClick={randomSearch}>Popular searches</div>
      </div>

      <Footer />
    </div>
  );
}
export default Sidebar;
