import React, { useState, useEffect } from 'react';
import Background from '../assets/movies2.jpg';
import './Register.css';
import axios from 'axios';
import { Button, FormGroup, FormControl } from "react-bootstrap";

export default function Register() {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  function validateForm() {
    if (password === password2){
      return true;
    }
    else {
      return false;
    }
  }

  const sendData = () => {
    console.log(userName,password,password2);
    const userData = {
      userName: userName,
      password: password
    };
    axios.post('http://localhost:8080/register', userData).catch(error => {
      console.log(error);
    });
  };

  function handleSubmit(event) {
    event.preventDefault();
    sendData();
    console.log("HELLO");
  }

  return (
    <div className="login-container">
      <div className="Login">
        <form onSubmit={handleSubmit}>
          <FormGroup controlId="text" bsSize="large" className="text-container">
            <p className="label">Email</p>
            <FormControl className="text-box"
              autoFocus
              type="text"
              value={userName}
              onChange={e => setUserName(e.target.value)}
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="large" className="text-container">
            <p className="label">Password</p>
            <FormControl className="text-box"
              value={password}
              onChange={e => setPassword(e.target.value)}
              type="password"
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="large" className="text-container">
            <p className="label">Password</p>
            <FormControl className="text-box"
              value={password2}
              onChange={e => setPassword2(e.target.value)}
              type="password"
            />
          </FormGroup>
          <Button block bsSize="large" className="button" disabled={!validateForm()} type="submit">
            Register
          </Button>
        </form>
      </div>
    </div>
  );
}
