import React, { useContext } from 'react';
import './Sidebar.css';
import Logo from '../assets/Logo.jpeg';
import axios from 'axios';
import { MovieContext } from '../Provider/MovieProvider';
import Footer from '../Footer/Footer';
import { Link } from 'react-router-dom';

function Sidebar() {
  const randomIds = [
    'tt0111161',
    'tt0068646',
    'tt0071562',
    'tt0468569',
    'tt0050083',
    'tt0108052',
    'tt0167260',
    'tt0110912',
    'tt0060196',
    'tt0120737',
    'tt0137523',
    'tt0109830',
    'tt1375666',
    'tt0080684',
    'tt0167261',
    'tt0133093',
    'tt0099685',
    'tt0073486',
    'tt0047478',
    'tt0114369',
  ];
  const [movieData, setMovieData] = useContext(MovieContext);

  const getRandomID = () => {
    return randomIds[Math.floor(Math.random() * randomIds.length)];
  };

  const handleValue = (e) => {
    let input = e.currentTarget.textContent;
    search(input);
  };

  const search = (input) => {
    let value = input;
    const url = `http://www.omdbapi.com/?apikey=8a2bf193&s=` + value;
    axios.get(url).then((res) => {
      setMovieData(res.data.Search);
    });
  };

  return (
    <div className='sidebar'>
      <Link to={'/'}>
        <div className='logo-container'>
          {' '}
          <img className='logo-img' src={Logo} alt='logo' />
        </div>
      </Link>
      <div className='sidebar-searches'>Popular searches</div>

      <ul className='popular-searches'>
        <Link to={`/`} onClick={handleValue}>
          <li className='popular-movie' onClick={handleValue}>
            Superman
          </li>
        </Link>
        <Link to={`/`} onClick={handleValue}>
          <li onClick={handleValue}>Star Wars</li>
        </Link>
        <Link to={`/`} onClick={handleValue}>
          <li onClick={handleValue}>Harry Potter</li>
        </Link>
        <Link to={`/`} onClick={handleValue}>
          <li onClick={handleValue}>Twilight</li>
        </Link>
      </ul>
      <div>
        <Link to={`/movie/${getRandomID()}`}>
          <div className='random-button'>Random Movie</div>
        </Link>
      </div>

      <Footer />
    </div>
  );
}
export default Sidebar;
