import React, { useContext, useState } from 'react';
import './Sidebar.css';
import Logo from '../assets/Logo.jpeg';
import axios from 'axios';
import { MovieContext } from '../Provider/MovieProvider';

function Sidebar() {
  const [movieData, setMovieData] = useContext(MovieContext);
  const [input, setInput] = useState('');

  const handleValue = (e) => {
    let input = e.currentTarget.textContent;
    console.log('input: ' + input);
    setInput(input);
    searchSuperman();
  };

  const searchSuperman = () => {
    let value = input;
    console.log(value);
    const url = `http://www.omdbapi.com/?apikey=8a2bf193&s=Superman`;
    axios.get(url).then((res) => {
      setMovieData(res.data.Search);
    });
  };
  return (
    <div className='sidebar'>
      <div className='logo-container'>
        {' '}
        <img className='logo-img' src={Logo} alt='logo' />
      </div>
      <div className='sidebar-searches'>Popular searches</div>
      <ul className='popular-searches'>
        <li onClick={handleValue} value='Superman'>
          Superman
        </li>
        <li>Star Wars</li>
        <li>Star Wars</li>
        <li>Star Wars</li>
      </ul>
    </div>
  );
}
export default Sidebar;
